import React from "react";
import { useState, useEffect } from "react";
import CharactersContainer from "./CharactersContainer";

function App() {
  const [characters, setCharacters] = useState([]);

  useEffect(() => {
    const headers = {
      Accept: "application/json",
      Authorization: "Bearer xvi06TocPJvBmrQC4yZv",
    };

    const fetchData = async () => {
      const rawCharacters = await fetch(
        "https://the-one-api.dev/v2/character",
        { headers: headers }
      );
      const charactersData = await rawCharacters.json(); // All Characters
      const characters = charactersData.docs;
      setCharacters(characters);
    };

    fetchData();
  }, []);

  function Search() {
    const {name} = useState("");
    function SearchForm() {
      return (
        <input type={name} name="name" placeHolder="Please enter the name" />
      )
    }

    // function Filter() {
    //   // TODO
    // }
    return (
      <div className="search-container">
        <SearchForm />
        <h1>This is the Search Components</h1>
      </div>
    );
  }

  return (
    <div className="App">
      <h1>People of Middle-Earth</h1>
      <Search />
      <CharactersContainer characters={characters} />
    </div>
  );
}

export default App;
