import React from "react";
import { useState, useEffect } from "react";
// import CharactersContainer from "./CharactersContainer";
// import Search from "./Search";

function App() {
  const [characters, setCharacters] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    const headers = {
      Accept: "application/json",
      Authorization: "Bearer xvi06TocPJvBmrQC4yZv",
    };

    fetch(
      "https://the-one-api.dev/v2/character",
      { headers: headers })
      .then((res) => res.json())
      .then((data) => console.log(data.doc));

    // const fetchData = async () => {
    //   const rawCharacters = await fetch(
    //     "https://the-one-api.dev/v2/character",
    //     { headers: headers }
    //   );
    //   const charactersData = await rawCharacters.json(); // All Characters
    //   const characters = charactersData.docs;
    //   setCharacters(characters);
    // };
    // fetchData();
  }, []);

  function Character({ character }) {
    return (
      <div className="character">
        <h3>{character.name}</h3>
        <p>{character.wikiUrl}</p>
        <p>{character.race}</p>
        <p>{character.gender}</p>
      </div>
    );
  }

  function CharactersContainer({ characters }) {
    const [searchResults, setSearchResults] = useState([]);
    // console.log(characters.map((character) => character.name))
    const results = characters.filter((character) => {
      character.name.toLowerCase().includes(searchTerm);
    });

    return (
      <div className="character-container">
        {characters.map((character) => (
          <Character key={character._id} character={character} />
        ))}
      </div>
    );
  }

  function SearchForm() {
    const { name } = setSearchTerm;

    function updateSearch(e) {
      setSearchTerm(e.target.value);
    }

    return (
      <div className="search-form">
        <input
          type="text"
          name="name"
          value={searchTerm}
          onChange={updateSearch}
          placeholder="Please enter the name"
        />
        <input type="submit" value="Submit" />
      </div>
    );
  }

  function SearchContainer() {
    return (
      <div className="search-container">
        <SearchForm />
        <h1>This is the Search Components</h1>
        <Filter />
      </div>
    );
  }

  function Filter() {
    return (
      <div className="filter">
        <h2>This is the Filter Component</h2>
      </div>
    );
  }

  return (
    <div className="App">
      <h1>People of Middle-Earth</h1>
      <SearchContainer />
      <CharactersContainer characters={characters} />
    </div>
  );
}

export default App;
